import UIKit
// swiftlint:disable nesting identifier_name

/// Models for creating and editing an object.
enum CreateAndEditObject {
	// MARK: Use cases

	enum FetchObject {
		struct Request {
			var id: String
		}
		struct Response {
			var object: Object
		}
		struct ViewModel {
			let title = "Edit Object"
			struct DisplayedObject {
				var type: String
				var name: String
				var description: String
				var relations: Set<String>
				var id: String?
			}
			var object: DisplayedObject
			var relations: [DisplayedObject]
		}
	}

	enum CreateObject {
		struct Request {
			var type: String
			var name: String
			var description: String
			var relations: Set<String>
		}
		struct Response {
			var object: Object
		}
		struct ViewModel {
			let title = "Create Object"
		}
	}

	enum UpdateObject {
		struct Request {
			var type: String
			var name: String
			var description: String
			var id: String
			var relations: Set<String>
		}
		struct Response {
			var object: Object
		}
		struct ViewModel { }
	}
}
